targets.query:
from(bucket: "github_webhooks")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "push")
  |> pivot(
    rowKey:["_time"],
    columnKey: ["_field"],
    valueColumn: "_value"
  )
  |> map(fn: (r) => ({
      _time: r._time,
      _value: r.message,
      _repo_name: r.repo_name,
      _sender: r.sender,
      _sha: r.sha,
      _count: 1,
    })
  )
  |> unique(column: "_sha")
  |> filter(fn: (r) => r._value =~ /.*typo.*/)
  |> group(columns: ["_sender"], mode:"by")
  |> stateCount(
    fn: (r) => r._count == 1,
    column: "count"
  )
  |> max(column: "count")
  |> map(fn: (r) => ({
      count: r.count,
      sender: r._sender,
    })
  )
  |> sort(columns: ["count"], desc: true)
